// SVG, PNG, JPG and WebP image types
declare module "*.svg" {
  const content: any;
  export default content;
}

declare module "*.png" {
  const content: any;
  export default content;
}

declare module "*.jpg" {
  const content: any;
  export default content;
}

// Webp
declare module "*.webp" {
  const content: any;
  export default content;
}
